import React, { useState, useEffect } from "react";
import axios from "axios";
import { Button, Avatar } from "@mui/material";

function VideoDetailChannel({ data }) {
  const [subscribe, setSubscribe] = useState(false);
  const [channel, setChannel] = useState();
  const {snippet} = data;

    function fetchChannel(id) {
    axios
      .get(`https://youtube.googleapis.com/youtube/v3/channels`, {
        params: {
            part: "snippet",
          id: `${snippet.channelId}`,
          key: "AIzaSyCeQDuZBJ00R2kC0cqvxWX86ZcVfpB7HVY",
        },
      })
      .then((res) => {
        if (res.status === 200) {
          setChannel(res.data);
        }
      })
      .catch((err) => console.log(err));
  };
  console.log(snippet)
  useEffect(() => {
    fetchChannel(snippet.channelId);
  }, [data]);
  return (
    <div className="container">
      <div className="info">
        <div className="logo">
          <Avatar
        //   src={channel.snippet.thumbnails.medium.url}
          />
        </div>
        <div className="title">{/* <p>{channel.title}</p> */}</div>
      </div>
      <div className="subscribe__btn">
        <Button
          onClick={() => setSubscribe(!subscribe)}
          color={subscribe ? "inherit" : "error"}
          variant="contained"
        >
          {subscribe ? "You are subscribed" : "SUBSCRIBE"}
        </Button>
      </div>
    </div>
  );
}

export default VideoDetailChannel;
