import React, { useState, useEffect } from "react";
import { Button } from "@mui/material";

function VideoDetailChannel() {
  const [subscribe, setSubscribe] = useState(false);
  const [channel, setChannel] = useState([]);

  const fetchChannel = async (id) => {
    if (data) {
      await axios
        .get(`https://youtube.googleapis.com/youtube/v3/channels`, {
          params: {
            id: id,
            key: "AIzaSyCeQDuZBJ00R2kC0cqvxWX86ZcVfpB7HVY",
          },
        })
        .then((res) => {
          if (res.status === 200) {
            setChannel(res.data.items[0].snippet);
          }
        })
        .catch((err) => console.log(err));
    }
  };

  useEffect(() => {
    fetchChannel()
  }, [])
  return (
    <div className="container">
      <div className="info">
        <div className="logo">
          <Avatar
          // src={channel.snippet.thumbnails.medium.url}
          />
        </div>
        <div className="title">
          <p>{channel.title}</p>
        </div>
      </div>
      <div className="subscribe__btn">
        <Button
          onClick={() => setSubscribe(!subscribe)}
          color={subscribe ? "inherit" : "error"}
          variant="contained"
        >
          {subscribe ? "You are subscribed" : "SUBSCRIBE"}
        </Button>
      </div>
    </div>
  );
}

export default VideoDetailChannel;
