import React, { useState } from "react";
import { Paper, TextField, Grid, InputAdornment } from "@mui/material";
import { Search, YouTube } from "@mui/icons-material";
import "./SearchBar.css";
function SearchBar({formSubmit}) {
  const [searchText, setSearchText] = useState("");

 const handleSubmit = (event) => {
    event.preventDefault();
    formSubmit(searchText);
 }

  return (
    <Paper elevation={3} sx={{ padding: "5px 20px" }}>
      <Grid container spacing={2}>
        <Grid item xs={8} sx={{display: "flex"}}>
            <span>
              <YouTube  sx={{ color: "red" }} fontSize="large"/>
            </span>
          <h3>
            YouTube
          </h3>
        </Grid>
        <Grid item xs={4} align="right">
          <form onSubmit={handleSubmit}>
            <TextField
              onChange={(e) => setSearchText(e.target.value)}
              placeholder="Search ..."
              size="small"
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Search />
                  </InputAdornment>
                ),
              }}
            />
          </form>
        </Grid>
      </Grid>
    </Paper>
  );
}

export default SearchBar;
