// https://youtube.googleapis.com/youtube/v3/channels?part=snippet&id=UCBa659QWEk1AI4Tg--mrJ2A&key=[YOUR_API_KEY]      channel
function VideoDetail() {
  const { id } = useParams();
  const [data, setData] = useState([]);
  let randomColor = require("randomcolor"); // import the script

  useEffect(() => {
    axios
      .get(`https://youtube.googleapis.com/youtube/v3/videos?part=snippet`, {
        params: {
          id: id,
          key: "AIzaSyCeQDuZBJ00R2kC0cqvxWX86ZcVfpB7HVY",
        },
      })
      .then((res) => {
        setData(res.data.items[0]);
      })
      .catch((err) => console.log(err));
  }, []);

  console.log(data);
  return (
    <>
      <Grid container spacing={2} p={3}>
        {[data].map((item) => (
          <Grid item xs={8}>
            <Grid container>
              <Grid item xs={12}>
                <iframe
                  width="100%"
                  height="494"
                  src={`https://www.youtube.com/embed/${id}`}
                  title="YouTube video player"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture;"
